var token=msg.req.headers.token
var ReturnID = msg.req.query.returnID || msg.req.body.returnID

var soapString = '<soapenv:Envelope xmlns:soapenv="http://schemas.xmlsoap.org/soap/envelope/" xmlns:xsd="http://www.w3.org/2001/XMLSchema" xmlns:xsi="http://www.w3.org/2001/XMLSchema-instance"><soapenv:Header><RequesterCredentials  xmlns="urn:ebay:apis:eBLBaseComponents"><eBayAuthToken>'+token+'</eBayAuthToken></RequesterCredentials></soapenv:Header><soapenv:Body>'
soapString += '<CSGetReturnDetailRequest xmlns="urn:ebay:apis:eBLBaseComponents">'
soapString += '<Version>999</Version>'
soapString += '<ReturnID>' + ReturnID + '</ReturnID>'
soapString += '<ReturnRole>SYSTEM</ReturnRole>'
soapString += '</CSGetReturnDetailRequest></soapenv:Body></soapenv:Envelope>'

msg.payload=soapString;
msg.rejectUnauthorized=false
msg.headers = {};
msg.headers={ 
    'SOAPAction': 'NoneRequired',
    'Content-Type': 'text/xml'
};
node.warn(msg)
return msg;

var xml = msg.payload
node.error(msg.payload)
msg.caseflag = false;
msg.findflag = false;
if (xml['soapenv:Envelope']['soapenv:Body'][0]['CSGetReturnDetailResponse'] !== undefined) {
    var response = xml['soapenv:Envelope']['soapenv:Body'][0]['CSGetReturnDetailResponse'][0]
    if (response.Ack[0] == 'Success') {
        var EBPCaseID = response.ReturnSummary[0].EBPCaseID[0];
        var ReturnState = response.ReturnSummary[0].ReturnState[0];
        var ReturnStatus = response.ReturnSummary[0].ReturnStatus[0];
        var DeliveryStatus = response.ReturnDetail[0].BuyerReturnShipment[0].DeliveryStatus
        if (DeliveryStatus) {
            DeliveryStatus = DeliveryStatus[0]
        } else {
            DeliveryStatus = ""
        }
        var sellerId = response.ReturnSummary[0].Seller[0].MemberID[0].UserOracleID[0]
        var sellerName = response.ReturnSummary[0].Seller[0].MemberID[0].UserID[0]
        var itemId = response.ReturnDetail[0].ItemDetails[0].ItemID[0]
        msg.req.query.claimID = EBPCaseID
        msg.req.query.User_id = sellerId
        msg.req.query.itemId = itemId
        if (isNaN(EBPCaseID)) {
            msg.caseflag = true
            msg.result = { EBPCaseID: "", DeliveryStatus: "", ReturnState: "", ReturnStatus: "", SellerId: sellerId, SellerName: sellerName, ItemId: itemId }
        } else {

            msg.result = { EBPCaseID: EBPCaseID, DeliveryStatus: DeliveryStatus, ReturnState: ReturnState, ReturnStatus: ReturnStatus, SellerId: sellerId, SellerName: sellerName, ItemId: itemId }
        }


    } else {
        msg.findflag = true;
        msg.caseflag = true
        msg.result = { EBPCaseID: "", DeliveryStatus: "", ReturnState: "", ReturnStatus: "", SellerId: "", SellerName: "", ItemId: "" }

    }
} else {
    msg.findflag = true;
    msg.caseflag = true
    msg.result = { EBPCaseID: "", DeliveryStatus: "", ReturnState: "", ReturnStatus: "", SellerId: "", SellerName: "", ItemId: "" }
}
return msg;
